import React, { Fragment, useEffect, useState } from "react";


const ListInfo = () => {
    const [information, setDescription] = useState("");//data getting from the input,, ("") default is a empty string..
    const [info1, setInfo] = useState([]);

  const getclientInformation = async (e) => {
    if(e) { e.preventDefault()}
    // e.preventDefault()
    if (information && information.trim().length > 0) {
        try {
            const response = await fetch(`http://localhost:8000/Client_Financials/?information=${information}`);
            const jsonData = await response.json();//await,since it takes little time to parse the data
          //   console.log
            setInfo(jsonData);
          } catch (err) {
            console.error(err.message);
          }
    } else {
        setInfo([])
    }
  };
//useEffect will make a fetch reuquest to restful apis every time 
//the current component is rendered...
  useEffect(() => {

    // if (information && information.trim().length > 0) {
        getclientInformation();
    // }  
  }, []);//  there are lot requests, hence using [] we make only one request

  console.log(info1);

  return (
    <Fragment>

       {/* Form */}
    <h1 className="text-center mt-5">Sask Fund Application</h1>
      <form className="d-flex mt-5" onSubmit={getclientInformation}>
        <input
          type="text"
          className="form-control"
          value={information}
          onChange={e => setDescription(e.target.value)} // onChange is required to change the value,i.e it allows to add in the text field..
        />
        <button className="btn btn-success">Get Info</button>
      </form>
        {/* form end */}


      {" "}
      <table class="table mt-5 text-center">
        <thead>
          <tr>
             <th>Client Id</th>
             <th>Client Information</th>
            {/* <th>Edit</th>
            <th>Delete</th> */}
          </tr>
        </thead>
        <tbody>
          {/* <tr>
            <td>John</td>
            <td>Doe</td>
            <td>john@example.com</td>
          </tr> */}
          {info1 ? info1.map(info1 => (
              <tr key={info1.client_id}>
                <td>{info1.client_id}</td>
                <td>{info1.information}</td>
              </tr>

          )): ""}
        </tbody>
      </table>

    </Fragment>
  );
};

export default ListInfo;
