import React, { Fragment, useEffect, useState } from "react";
// import EditTodo from "./EditTodo";

const ListInfo = () => {
  const [info, setTodos] = useState([]);
  const [information, setDescription] = useState("");


  const getclientInformation = async () => {
    try {
      const response = await fetch("http://localhost:8000/Client_Financials");
      const jsonData = await response.json();//await,sine it takes little time to parse the data

      setTodos(jsonData);
    } catch (err) {
      console.error(err.message);
    }
  };
//useEffect will make a fetch reuquest to restful apis every time 
//the current component is rendered...
  useEffect(() => {
    getclientInformation();
  }, []);//  there are lot requests, hence using [] we make only one request

  console.log(info);

  return (
    <Fragment>


       {/* Form */}
        <h1 className="text-center mt-5">Sask Fund Application</h1>
      <form className="d-flex mt-5" onSubmit={getclientInformation}>
        <input
          type="text"
          className="form-control"
          value={information}
          onChange={e => setDescription(e.target.value)}
        />
        <button className="btn btn-success">Get Info</button>
      </form>
        {/* form end */}


      {" "}
      <table class="table mt-5 text-center">
        <thead>
          <tr>
             <th>Client Id</th>
             <th>Client Information</th>
            {/* <th>Edit</th>
            <th>Delete</th> */}
          </tr>
        </thead>
        <tbody>
          {/* <tr>
            <td>John</td>
            <td>Doe</td>
            <td>john@example.com</td>
          </tr> */}
          {info.map(info => (
            <tr key={info.client_id}>
              <td>{info.client_id}</td>
              <td>{info.information}</td>
            </tr>

          ))}
        </tbody>
      </table>
    </Fragment>
  );




};

export default ListInfo;
