import React, { Fragment, useEffect, useState } from "react";
// import EditTodo from "./EditTodo";

const ListInfo = () => {
  const [info, setTodos] = useState([]);

  const getclientInformation = async () => {
    try {
      const response = await fetch("http://localhost:8000/Client_Financials");
      const jsonData = await response.json();

      setTodos(jsonData);
    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect(() => {
    getclientInformation();
  }, []);

  console.log(info);

  return (
    <Fragment>
      {" "}
      <table class="table mt-5 text-center">
        <thead>
          <tr>
            <th>Description</th>
            <th>Edit</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {/*<tr>
            <td>John</td>
            <td>Doe</td>
            <td>john@example.com</td>
          </tr> */}
          {info.map(info => (
            <tr key={info.client_id}>
              <td>{info.information}</td>
              {/* <td>
                <EditTodo info={info} />
              </td> */}
              {/* <td>
                <button
                  className="btn btn-danger"
                  onClick={() => deleteTodo(info.client_id)}
                >
                  Delete
                </button>
              </td> */}
            </tr>
          ))}
        </tbody>
      </table>
    </Fragment>
  );
};

export default ListInfo;
