import React, { Fragment, useEffect, useState } from "react";


const Client_Info = () => {
  const [info, setInfo] = useState([]);

  //delete todo function
  const getInfo = async () => {
    try {
      const response = await fetch("http://localhost:5000/todos");
      const jsonData = await response.json();

      setInfo(jsonData);
    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect(() => {
    getInfo();
  }, []);

  console.log(info);

  return (
    <Fragment>
       {/* Form */}
    <h1 className="text-center mt-5">Sask Fund Application</h1>
      <form className="d-flex mt-5" onSubmit={getInfo}>
        <input
          type="number"
          className="form-control"
          placeholder="Enter Client ID"
          value={info}
          onChange={e => setDescription(e.target.value)} // onChange is required to change the value,i.e it allows to add in the text field..
        />

        <button className="btn btn-success">Check Eligibility</button>
      </form>
      {" "}
      <table class="table mt-5 text-center">
        <thead>
          <tr>
            <th>Client Name</th>
            <th>Client Id</th>
            <th>Gender</th>
            <th>Age</th>
            <th>City</th>
            <th>Golden Membership</th>
          </tr>
        </thead>
        <tbody>
          {/*<tr>
            <td>John</td>
            <td>Doe</td>
            <td>john@example.com</td>
          </tr> */}
          {info.map(info => (
            <tr key={info.client_id}>
              <td>{info.name}</td>
              <td>
               {info.client_id}
              </td>
              <td>
                {info.age}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </Fragment>
  );
};

export default Client_Info;
